# First go to the user home directory. $ cd ~ $ pwd /Users/yuxizheng # Edit the user .bash_pforile file. $ sudo vim .bash_profile # Add below content at the end of the .bash_profile file. Save and exit. # Add MySQL binary folder in the macOS system environment variable PATH. PATH=&#34;/usr/local/mysql/bin:${PATH}&#34; export PATH # Verify the .bash_profile has been changed as required. $ cat .bash_profile # Make the above changes take effect in macOS. $ source .bash_profile # Now you can run mysql command directly in terminal to connect to the MySQL database server. $ mysql -u root -p # login # /usr/local/mysql/bin/mysql -u root -p mysql -u root -p mysql --local-infile=1 -u root -p A1b2C3d4&amp; # set password SET PASSWORD = PASSWORD(&#39;[newpassword]&#39;); SET PASSWORD = PASSWORD(&#39;[1234567890]&#39;); SET GLOBAL local_infile = true; # show databases show databases; # select database use &lt;db&gt; # list tables show tables; # show table describe &lt;table&gt;; # load data load data local infile &#39;path to the csv file&#39; into table &lt;table&gt; fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; # exit mysql exit ERROR 2068 (HY000): LOAD DATA LOCAL INFILE file request rejected due to restrictions on access
load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/cs564/lab1/universitydb/Enroll.csv&#39; into table Enroll fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; // 13 select s.sname from student s where s.snum in ( select e1.snum from enroll e1, enroll e2, class c1, class c2 where c1.cname != c2.cname AND c1.meets_at = c2.meets_at AND e1.snum = e2.snum AND e1.cname = c1.cname AND e2.cname = c2.cname); // 20 select s1.age, s1.level from student s1 group by s1.age, s1.level having s1.level in ( select s2.level from student s2 where s1.age = s2.age group by s2.age, s2.level having count(*) &gt;= ALL( select count(*) from student s3 where s2.age = s3.age group by s3.age, s3.level)); // 17 select f.fname, count(c.cname) AS num from class c, faculty f where c.fid = f.fid group by f.fname having f.fname NOT IN (select f1.fname from class c1, faculty f1 where c1.fid = f1.fid and c1.cname NOT IN (select c2.cname from class c2 where c2.room = &#39;R128&#39;)); CREATE TABLE assets ( symbol VARCHAR(50), date DATE, open DOUBLE, high DOUBLE, low DOUBLE, close DOUBLE, volume DOUBLE, PRIMARY KEY (symbol, date)); load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/commo_merged.csv&#39; into table assets fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/stock_merged.csv&#39; into table assets fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/crypto_merged.csv&#39; into table assets fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; CREATE TABLE commo ( symbol VARCHAR(50), name VARCHAR(50), PRIMARY KEY (symbol)); load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/_metadata_commo.csv&#39; into table commo fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; CREATE TABLE stock ( symbol VARCHAR(50), name VARCHAR(50), country VARCHAR(50), continent VARCHAR(50), PRIMARY KEY (symbol)); load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/_metadata_stock.csv&#39; into table stock fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; CREATE TABLE crypto ( symbol VARCHAR(50), name VARCHAR(50), PRIMARY KEY (symbol)); load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/_metadata_crypto.csv&#39; into table crypto fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; CREATE TABLE login ( user VARCHAR(50), password VARCHAR(50), PRIMARY KEY (user, password)); CREATE TABLE watchlist ( user VARCHAR(50), symbol VARCHAR(50), name VARCHAR(50), type VARCHAR(50), PRIMARY KEY (user, symbol)); CREATE TABLE currency ( name VARCHAR(50), symbol VARCHAR(50), date DATE, price VARCHAR(50), PRIMARY KEY (symbol, date)); load data local infile &#39;/Users/yuxizheng/xizheng/2022-SUMMER/CS564-Final-Project/dataset/exchangeRate_merged.csv&#39; into table currency fields terminated by &#39;,&#39; lines terminated by &#39;
&#39;; CREATE TABLE country ( symbol VARCHAR(50), name VARCHAR(50), continent VARCHAR(50), PRIMARY KEY (symbol)); SELECT a.symbol, AVG(a.close) FROM assets a, commo c Where c.symbol = a.symbol AND a.symbol IN ( SELECT DISTINCT symbol FROM assets WHERE date BETWEEN &#39;2005-01-01&#39; AND &#39;2005-12-31&#39;) GROUP BY a.symbol ORDER BY AVG(a.close) ASC LIMIT 5; SELECT c.symbol, COUNT(a.date) FROM assets a, commo c Where c.symbol = a.symbol GROUP BY c.symbol ORDER BY COUNT(a.date) DESC LIMIT 10; mysqldump -u root -p 564project &gt; ./sql-data-backup.sql
